import React, { Component, useEffect, useReducer, useState, Fragment, useRef } from "react"
import ReactDOM from "react-dom"
import './App.css'
import { TabPanel, TabContext, TabList } from '@mui/lab'
import Box from '@mui/material/Box'
import PermIdentity from '@mui/icons-material/PermIdentity'
import WorkOutline from '@mui/icons-material/WorkOutline'
import Tab from '@mui/material/Tab'
import axios from 'axios'
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import {Grid} from '@mui/material'
import AppBar from '@mui/material/AppBar'
import Toolbar from '@mui/material/Toolbar'
import Typography from '@mui/material/Typography'
import Button from '@mui/material/Button'
import IconButton from '@mui/material/IconButton'
import MenuIcon from '@mui/icons-material/Menu'
import { Draggable } from "react-drag-reorder"


function  App (){  

  const [tabValue, settabValue]=useState(0)
  const [promotions, setPromotions]=useState(JSON.parse(localStorage.getItem('sp_promotions')) != null ? JSON.parse(localStorage.getItem('sp_promotions')):[])

  const handleChange = (event, newValue) => {
    settabValue(newValue)
  }
  useEffect(() => {
    if(promotions.length==0)
    axios
    .get(
      "https://run.mocky.io/v3/484016a8-3cdb-44ad-97db-3e5d20d84298"       
    )
    .then((response) => {
     localStorage.setItem('sp_promotions', JSON.stringify(response.data))
     setPromotions(response.data)
    })
      }, [])  

  const state = {
    words: ["Hello", "Hi", "How are you", "Cool"],
  };

 const getChangedPos = (currentPos, newPos) => {
    console.log(currentPos, newPos);
  };

    return (
      <>
      <Box sx={{ flexGrow: 1 }}>
      <AppBar position="fixed">
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            PROMOTIONS
          </Typography>
          <Button color="inherit">Welcome User</Button>
        </Toolbar>
      </AppBar>
    </Box>
      <div className="flex-container">
        <div className="row">
          <Draggable onPosChange={getChangedPos}>
            {state.words.map((word, idx) => {
              return (
                <div key={idx} className="flex-item">
                  {word}
                </div>
              );
            })}
          </Draggable>
        </div>
      </div>
      </>
    );

}

export default App;
ReactDOM.render(<App />, document.getElementById("root"));
