import React, { useEffect, useReducer, useState, Fragment, useRef } from 'react'
import logo from './logo.svg';
import './App.css';
import { TabPanel, TabContext, TabList } from '@mui/lab'
import Box from '@mui/material/Box'
import PermIdentity from '@mui/icons-material/PermIdentity'
import WorkOutline from '@mui/icons-material/WorkOutline'
import Tab from '@mui/material/Tab'
import axios from 'axios'
import Container from 'react-bootstrap/Container'
import { Grid } from '@mui/material'

function App() {
  const [tabValue, settabValue]=useState(0)
  const promotions =JSON.parse(localStorage.getItem('sp_promotions'))

  const handleChange = (event, newValue) => {
    settabValue(newValue)
  }
  useEffect(() => {   
  axios
      .get(
        "https://run.mocky.io/v3/484016a8-3cdb-44ad-97db-3e5d20d84298"       
      )
      .then((response) => {
        console.log(response.data)
        localStorage.setItem('sp_promotions', JSON.stringify(response.data))
      })
    // empty dependency array means this effect will only run once (like componentDidMount in classes)
  }, [])


  return (
    <Grid
    container
    columnSpacing={1}
    alignContent="center"
    justifyContent="center"
      >
      <TabContext value={tabValue}>
        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <TabList onChange={handleChange}>
            <Tab value={0} icon={<WorkOutline />} label="All Promotions" />
            <Tab value={1} icon={<PermIdentity />} label="New Customer" />
            </TabList>
            </Box>
      </TabContext>
      </Grid>
  );
}

export default App;
